HistoricSitesNyc.create!([
  {name: "Van Cortlandt House Museum", address: "Broadway at West 246th Street", park_name: "Van Cortlandt Park", phone: "(718) 543-3344", description: "As New York City&rsquo;s first historic house museum, Van Cortlandt House contains an outstanding collection of furniture and decorative arts, including many heirlooms donated back to the house by members of the Van Cortlandt family. In 1694, Jacobus Van Cortlandt, a merchant and Mayor of New York (1710-11, 1719-20), purchased the parcel of land that today contains Van Cortlandt Park and the Van Cortlandt House Museum. His son Frederick built the current house, the oldest in the Bronx, in 1748-49. George Washington stayed at the Van Cortlandt House at least twice during the Revolutionary War. Van Cortlandt descendants lived in the House until 1886, when they sold the entire estate to the City of New York for Van Cortlandt Park. In 1896, The National Society of Colonial Dames in the State of New York restored the House as a museum and operates it to this day.", accessible: "N", historic_house_trust_website: "http://historichousetrust.org/house/van-cortlandt-house-museum/", url: "http://www.vchm.org/", property_id: "X092", latitude: 40.8911764, longitude: -73.8948066, image_url: "https://upload.wikimedia.org/wikipedia/commons/thumb/8/88/Van-cortland-house-bronx.JPG/800px-Van-cortland-house-bronx.JPG", alt_image_url: "http://www.vchm.org/uploads/2/5/7/5/25751801/6560461.jpg?621"},
  {name: "Valentine-Varian House", address: "3266 Bainbridge Avenue at East 208th Street", park_name: "Varian Park", phone: "(718) 881-8900", description: "The second oldest house in the Bronx, the Valentine-Varian House was built by blacksmith and farmer Isaac Valentine in 1758, a time when carriages traveled the nearby Boston Post Road through a Bronx that was still mostly farmland. During the Revolutionary War, Valentine and his family had to abandon their two-story fieldstone home, which was occupied by British, Hessian, and American troops. Though close to several fierce battles with cannons clustered on a nearby hill, the house miraculously survived. In 1965, the house was donated to The Bronx County Historical Society, moved diagonally across the street to its present location on a new foundation, and now operates as the Museum of Bronx History.", accessible: "N", historic_house_trust_website: "http://historichousetrust.org/house/valentine-varian-house/", url: "http://bronxhistoricalsociety.org/museum-of-bronx-history/", property_id: "X215", latitude: 40.8773653, longitude: -73.8794392, image_url: "https://media2.wnyc.org/i/620/350/80/photologue/photos/1_______.jpg", alt_image_url: "https://nyhistorywalks.files.wordpress.com/2012/02/hawaii-2009-072.jpg"},
  {name: "Edgar Allan Poe Cottage", address: "East Kingsbridge Road and Grand Concourse", park_name: "Poe Park", phone: "(718) 881-8900", description: "The tiny Poe cottage in the Bronx, built by John Wheeler in 1812, was the last home of Edgar Allan Poe (1809-1849), the great American poet and author. Set in a small park on the Grand Concourse, it is the only house left from the old village of Fordham. Poe leased the one-and-a-half-story cottage for $100 a year and wrote many of his classic works there including 'The Bells,' 'Eureka' and 'Annabel Lee.' The cottage, saved from destruction in the 1890s by the Shakespeare Society and moved from its original location on the other side of Kingsbridge Road in 1913, preserves a precious chapter of New York's literary heritage.", accessible: "Y", historic_house_trust_website: "http://historichousetrust.org/house/edgar-allan-poe-cottage/", url: "http://bronxhistoricalsociety.org/poe-cottage/", property_id: "X040", latitude: 40.8655316, longitude: -73.8942093, image_url: "http://static.panoramio.com/photos/large/37477106.jpg", alt_image_url: "http://3.bp.blogspot.com/-SrvncDgaiKg/UHDtFz-DtiI/AAAAAAAADIw/eAJLSFM2vro/s1600/poe+cottage.jpg"},
  {name: "Gracie Mansion", address: "88th Street & East End Avenue", park_name: "Carl Schurz Park", phone: "(212) 570-4773", description: "Gracie Mansion, officially the residence of the Mayor of the City of New York, stands in Carl Schurz Park and is the last of the elegant country mansions that once lined Manhattan's East River shore. Archibald Gracie, a Scottish shipping magnate, bought the property in 1798 and built the mansion the following year. Elegant parties were held at the mansion that attracted Louis Philippe, later King of France; President John Quincy Adams; Washington Irving; and Rufus King, ambassador to Britain. In the late 1880s, the property was condemned and finally seized by the City in 1896. Gracie Mansion opened as the first Museum of the City of New York in 1924, and as a restored house museum in 1936. Fiorello LaGuardia made it the official Mayor's residence six years later. A 2002 restoration transformed Gracie Mansion into the 'People's House' and increased accessibility to the public and City agencies.", accessible: "Y", historic_house_trust_website: "http://historichousetrust.org/house/gracie-mansion/", url: "http://www1.nyc.gov/site/gracie/index.page", property_id: "M081", latitude: 40.776058, longitude: -73.9430817, image_url: "http://www1.nyc.gov/assets/gracie/images/content/hero/gracie-mansion.jpg", alt_image_url: "https://images.nymag.com/images/2/daily/2009/01/20090116_graciemansion_560x375.w710.h473.jpg"},
  {name: "Little Red Lighthouse", address: "178th Street and the Hudson River", park_name: "Fort Washington Park", phone: "(212) 304-2365", description: "The Jeffrey's Hook lighthouse, erected in 1880 and moved to its current site in 1921, has become widely known as the children's literary landmark 'The Little Red Lighthouse.' The story of the lighthouse in Fort Washington Park was popularized by the children's book The Little Red Lighthouse and the Great Gray Bridge by Hildegarde H. Swift with illustrations by Lynd Ward, published in 1942. The lighthouse became a celebrated 'child's landmark,' after the proposed removal of the lighthouse in 1951. The public outcry of children and their allies prompted the preservation of the structure through its transfer to the jurisdiction of the City of New York/Parks & Recreation.", accessible: "N", historic_house_trust_website: "http://historichousetrust.org/house/little-red-lighthouse/", url: "https://www.nycgovparks.org/parks/fort-washington-park/highlights/11044", property_id: "M028", latitude: 40.8502552, longitude: -73.9469689, image_url: "http://www.cestujzadara.cz/cestopisy/amerika/cestopisy-z-usa/oazy-klidu-v-rusnem-new-yorku/little-red-lighthouse-jpg.jpg", alt_image_url: "https://upload.wikimedia.org/wikipedia/commons/thumb/c/c0/GWB_and_Little_Red_Lighthouse_Xmas110013.jpg/375px-GWB_and_Little_Red_Lighthouse_Xmas110013.jpg"},
  {name: "Wyckoff Farmhouse Museum", address: "5816 Clarendon Road at Ralph Avenue", park_name: "Fidler-Wyckoff House Park", phone: "(718) 629-5400", description: "Once a stone's throw from salt marshes and clam beds, the Wyckoff Farmhouse Museum is probably the oldest home in New York City. The house, built around 1652, became the City's first landmark in 1965. The life of Pieter Claesen Wyckoff is an American success story. In 1637, he arrived in America, an illiterate indentured servant. He eventually became a magistrate, successful farmer, and the wealthiest citizen of New Amersfoot, which later became the town of Flatlands. Wyckoff's descendants lived in the house until 1901. The Wyckoff House Foundation bought back the house in 1961 and donated it to the City in 1969.", accessible: "Y", historic_house_trust_website: "http://historichousetrust.org/house/wyckoff-farmhouse-museum-education-center/", url: "http://www.wyckoffmuseum.org", property_id: "B376", latitude: 40.6443368, longitude: -73.9207731, image_url: "https://www.theclio.com/web/ul/11985.19689.jpg", alt_image_url: "http://www.nybeyondsight.org/img/portspics/wyckoff-farmhouse-museum.jpg"},
  {name: "The Old Stone House", address: "Fifth Avenue at Third Street", park_name: "J. J. Byrne Memorial Park", phone: "(718) 768-3195", description: "In the Park Slope/Gowanus neighborhood of Brooklyn stands a replica of a Dutch stone farmhouse with a very rich history. The original house was built by Nicholas Vechte beside the Gowanus Creek in 1699. Later, during the Revolutionary War, the house was taken amidst fierce fighting by an estimated 2,000 British and hired Hessian soldiers. In 1797, the house was sold to the Cortelyou family. Almost a century later, it became the first clubhouse of the Brooklyn team of the National Baseball League, later known as the Brooklyn Dodgers. By the 1890s, the house had been demolished but was rebuilt with the original stones in the 1930s to serve as a sports facility in the newly opened J. J. Byrne Park (now named Washington Park). Today, the Old Stone House offers a year-round program of education and cultural events.", accessible: "Y", historic_house_trust_website: "http://historichousetrust.org/house/old-stone-house/", url: "http://www.theoldstonehouse.org/", property_id: "B111", latitude: 40.6729885, longitude: -73.9846023, image_url: "http://explorebk.com/wp-content/uploads/2015/08/Old-Stone-House.jpg", alt_image_url: "http://theoldstonehouse.org/wp-content/uploads/2015/10/temp-slide-oshpainting-495x400.jpg"},
  {name: "Queens County Farm Museum", address: "73-50 Little Neck Parkway", park_name: "Queens Farm Park/Creedmoor Farm Park", phone: "(718) 347-FARM", description: "At the Queens County Farm Museum, people may visit a working farm surrounding the restored Jacob Adriance Farmhouse. The rich glacial soil of Flushing first attracted European settlers in the 17th century, and family farms characterized the area from then until the 1920s. The earliest records of farm ownership by the Adriance family on this site date from 1697. The third-generation Jacob Adriance built his Dutch-style farmhouse in 1772. The home was doubled in size by Peter Cox between 1833 and 1840. The Creedmoor State Hospital obtained the property in 1927, and for decades the area remained agricultural and was tilled by hospital patients; the house was used by hospital staff until 1973. Two years later, the Colonial Farmhouse Restoration Society of Bellerose opened the Queens County Farm Museum. Today, its 7.5 acres of croplands and orchards are being used to demonstrate the history of agriculture in New York.", accessible: "Y", historic_house_trust_website: "http://historichousetrust.org/house/queens-county-farm-museum/", url: "http://queensfarm.org", property_id: "Q453", latitude: 40.7481378, longitude: -73.7210428, image_url: "http://www.queensfarm.org/images/QCFMHomepagehigh-res_revised.jpg", alt_image_url: "http://queensfarm.org/images/slideshow/barn.jpg"},
  {name: "Lewis H. Latimer House", address: "34-41 137th Street", park_name: "(no park)", phone: "(718) 961-8585", description: "The Lewis H. Latimer House is a modest Queen Anne-style, wood-frame suburban residence constructed in Flushing, Queens between 1887 and 1889 by the Sexton family. Lewis Howard Latimer, an African-American inventor and electrical pioneer and the son of fugitive slaves, lived in the house from 1903 until his death in 1928. During his career, he worked with three of the greatest scientific inventors in American history, including Alexander Graham Bell, Hiram S. Maxim, and Thomas Alva Edison. He played a critical role in the development of the telephone and, as Edison's chief draftsman, he invented and patented the carbon filament, a significant improvement in the production of the incandescent light bulb. Over the course of his career, Latimer supervised the installation of street lighting and the construction of electric plants in many American cities, as well as London and Montreal. Today, exhibitions and public programs call attention to Latimer's and other African Americans' contributions to science, technology and American life.", accessible: "Y", historic_house_trust_website: "http://historichousetrust.org/house/lewis-h-latimer-house-museum/", url: "https://latimernow.org/", property_id: "Q497", latitude: 40.7661211, longitude: -73.8293949, image_url: "http://historichousetrust.org/wp-content/uploads/2014/10/Latimer-2-310x310.jpg", alt_image_url: "http://maap.columbia.edu/content/places/lewis_latimer/images/274/52_ABG7062_274.jpg"},
  {name: "Lefferts Historic House", address: "Flatbush Avenue at Empire Boulevard and Ocean Avenue, near the Willink entrance to the Park.", park_name: "Prospect Park", phone: "(718) 789-2822", description: "Located six blocks north of its original site on Flatbush Avenue near Maple Street, Lefferts Historic House in Prospect Park is one of the few surviving Dutch Colonial farmhouses in Brooklyn. Pieter Lefferts owned the house when it was built between 1777 and 1783 to replace an earlier family home burned during the battle of Long Island in 1776, and it served as home to at least three more generations of the Lefferts family, including his State Senator son John Lefferts and his granddaughter. In 1917, the estate of John Lefferts offered the House to the City of New York, and in 1920, it was opened as a museum by the Fort Greene chapter of the Daughters of the American Revolution. Today Lefferts Historic House is operated by the <a href='http://www.prospectpark.org/'>Prospect Park Alliance</a>", accessible: "N", historic_house_trust_website: "http://www.historichousetrust.org/lefferts-historic-house-museum/", url: "https://www.prospectpark.org/visit-the-park/places-to-go/lefferts-historic-house/", property_id: "B073", latitude: 40.6643449, longitude: -73.9637644, image_url: "http://www.projectaccessforall.org/sites/default/files/styles/article_full/public/L1000059_0.jpg?itok=DQg070dF", alt_image_url: "http://explorebk.com/wp-content/uploads/2015/09/Lefferts-Historic-House-Prospect-Park.jpg"},
  {name: "Bartow-Pell Mansion Museum", address: "895 Shore Road", park_name: "Pelham Bay Park", phone: "(718) 885-1461", description: "Nestled deep in the woods of Pelham Bay Park and only a mile from a network of bustling highways in the Bronx, the Bartow-Pell Mansion Museum is a rare example of country elegance in New York City. The Greek Revival house, originally occupied by publisher Robert Bartow and family in 1842, sits on an estate that dates back to 1654 when Thomas Pell, an English doctor from Connecticut, bought the land from the Siwanoy Indians. The property was acquired by the City in 1888, and the mansion was used by Mayor Fiorello LaGuardia as a summer office in 1936. It opened as a museum in 1946.", accessible: "N", historic_house_trust_website: "http://historichousetrust.org/house/bartow-pell-mansion-museum/", url: "http://www.bartowpellmansionmuseum.org", property_id: "X039", latitude: 40.8717862, longitude: -73.8055594, image_url: "http://www.cimages.me/sites/cimages.me/files/images/BPM521_3.jpg?1411150088", alt_image_url: "https://media-cdn.tripadvisor.com/media/photo-s/05/6b/26/2e/bartow-pell-mansion-museum.jpg"},
  {name: "Seguine Mansion", address: "440 Seguine Avenue", park_name: "Lemon Creek Park", phone: "(718) 667-6042", description: "Along the southern shore of Staten Island, the Seguine Mansion, a stately Greek Revival structure, faces Prince's Bay. Built in 1838 by Joseph H. Seguine, the house is a physical reminder of the classical architecture and thriving commerce of Staten Island during the mid-19th century. Sometime between 1780 and 1786, James Seguine bought a large parcel of land near Lemon Creek from Abraham Manee, whose French Huguenot family had held the land since about 1700. Joseph, James Seguine's grandson, and the fifth generation of the family on Staten Island, built the elegant two-and-a-half-story house, typical of high-style homes in the region at that time. Following Joseph's death in 1856, the house remained in the family until 1868. When it was sold, the amount of land had decreased to 10 acres. During the late 19th century, the building served as an inn or hotel, at a time when Prince's Bay had become a popular resort area. The house returned to Seguine descendants from 1916 through 1977 and was then sold at auction in 1981 to George Burke. Burke stabilized the deteriorated house in 1989 and donated it to the City of New York, while retaining a life interest.", accessible: "N", historic_house_trust_website: "http://historichousetrust.org/house/seguine-mansion/", url: "http://seguinemansion.org/", property_id: "R079", latitude: 40.5151966, longitude: -74.1975247, image_url: "http://seguinemansion.org/wp-content/uploads/2012/06/slide-home.jpg", alt_image_url: "http://media.silive.com/advance/photo/2011/09/10088291-large.jpg"},
  {name: "Conference House", address: "7455 Hylan Boulevard", park_name: "Conference House Park", phone: "(718) 984-6046", description: "At the southernmost tip of Staten Island&mdash;and New York State&mdash;stands the Conference House, a 17th-century stone manor. In 1676, British naval captain Christopher Billop was granted a 932-acre property known as the Manor of Bentley. Billop built the Conference House, a solid, two-story structure of native fieldstone, in about 1680. During the American Revolution, the owner of the manor was Captain Billop's great-grandson, a Tory colonel also called Christopher Billop. On September 11, 1776, his house was the site of peace negotiations between British Lord Admiral Richard Howe and Benjamin Franklin, John Adams, and Edward Rutledge. The talks were unsuccessful, and the war continued for seven more years. After the Revolution, the house was confiscated by the State of New York; it served as a multi-family dwelling, a 19th-century hotel, and as a rat-poison factory before being deeded to the City of New York in 1926. In 1929, the Conference House Association took over operation of the house and has taken care of it since that time.", accessible: "Y", historic_house_trust_website: "http://historichousetrust.org/house/conference-house/", url: "http://www.theconferencehouse.org/", property_id: "R006", latitude: 40.5031274, longitude: -74.253251, image_url: "http://28g161d5fzz289lfg30e1yrf.wpengine.netdna-cdn.com/wp-content/uploads/2011/11/houseinspring.jpg", alt_image_url: "https://www.google.com/url?sa=i&rct=j&q=&esrc=s&source=images&cd=&ved=0ahUKEwi0vPGpionUAhVIPBQKHbfuASoQjRwIBw&url=http%3A%2F%2Fwww.tmrives.com%2Fwriting%2F09.20.2012.html&psig=AFQjCNGSDLhghuXRoyXk1KrE_NSJ3P8ZWA&ust=1495733234846776"},
  {name: "Bowne House", address: "37-01 Bowne Street", park_name: "Weeping Beech Park", phone: "(718) 359-0528", description: "Built circa 1661, the Bowne House is a microcosm of social, cultural, and political history.  It is one of the oldest surviving structures in New York City and the oldest in Queens. The Bowne House is a fine example of mid-17th-century Anglo-Dutch architecture with an exceptional collection of furnishings, but its true magic is its story.  The house was built by John Bowne, a prominent Quaker and advocate of religious freedom, who emigrated from England to Boston in 1649 and eventually settled in Flushing, Queens.  The contributions of this family to New York City&rsquo;s heritage began with the courageous actions of John Bowne (1627-1695), who used the house as the first indoor meeting place for the Society of Friends, at a time when religious diversity was forbidden by law. The house and grounds are currently closed for restoration.", accessible: "N", historic_house_trust_website: "http://historichousetrust.org/house/bowne-house/", url: "http://www.bownehouse.org/index.htm", property_id: "Q022", latitude: 40.7628909, longitude: -73.8249531, image_url: "https://static01.nyt.com/images/2009/09/30/nyregion/bowne-480.jpg", alt_image_url: "https://www.theclio.com/web/ul/19673.39601.jpg"},
  {name: "Swedish Cottage Marionette Theatre", address: "79th Street and West Drive", park_name: "Central Park", phone: "(212) 988-9093", description: "The Swedish Cottage has been a destination for New York City children for decades. The Cottage, a model schoolhouse, was built as Sweden's exhibit for the 1876 Centennial Exposition in Philadelphia. In 1877, Frederick Law Olmsted moved the Cottage to its present site in Central Park. In 1947, it became the headquarters of Parks' Traveling Marionette Theatre, and in 1973, it was remodeled to include a permanent stage. Since then this small wooden lodge has hosted daily marionette shows that bring fairy tales alive for enchanted audiences. The Cottage is managed by City Parks Foundation.", accessible: "Y", historic_house_trust_website: "http://historichousetrust.org/house/swedish-cottage-marionette-theatre/", url: "http://www.cityparksfoundation.org/arts/swedish-cottage-marionette-theatre/", property_id: "M010", latitude: 40.7799586, longitude: -73.9703645, image_url: "https://i.ytimg.com/vi/Na7xkLBcWc0/maxresdefault.jpg", alt_image_url: "http://www.inetours.com/New_York/Images/Cntrl-Prk/Swedish_3882.jpg"},
  {name: "Historic Richmond Town", address: "441 Clarke Avenue", park_name: "La Tourette Park", phone: "(718) 351-1611", description: "Historic Richmond Town, in the heart of Staten Island, is a remarkable living history village and museum complex that encompasses three hundred years of the history and culture of New York City. The Town of Richmond, which began as a modest hamlet in the 1690s, became Staten Island's county seat of government in 1728. After Staten Island was incorporated into the City of New York in 1898, its government offices were moved to the town of St. George, and Richmond gradually became a quiet residential area. In 1935, the Staten Island Historical Society first opened New York City's only historic village. The Society acquired the 1848 County Clerk's office and established the Historical Museum. In 1939, the Society acquired and restored the Voorlezer's House (circa 1695), the nation's oldest building that served as a schoolhouse. Today, Richmond Town's 100 acres include 28 historic buildings dating from the late 17th to the early 20th centuries, half of which stand on their original locations. Historic Richmond Town is a joint project of the Staten Island Historical Society, a not-for-profit cultural organization, and the City of New York, which owns the park land and buildings and supports part of its operations with public funds provided through the Department of Cultural Affairs.", accessible: "Y", historic_house_trust_website: "http://historichousetrust.org/house/historic-richmond-town/", url: "http://www.historicrichmondtown.org/", property_id: "R013", latitude: 40.5709205, longitude: -74.1456038, image_url: "http://www.artcom.com/Museums/nv/gl/Historic%20RichmondCourthouse%20w%20visitors%20period%20and%20today.jpg", alt_image_url: "https://www.historicrichmondtown.org/images/ChristopherH1.jpg"},
  {name: "Morris-Jumel Mansion", address: "65 Jumel Terrace at 160th Street", park_name: "Roger Morris Park", phone: "(212) 923-8008", description: "Manhattan's oldest surviving house, the Morris-Jumel Mansion, atop Harlem Heights, is a monument to colonial grandeur. Built about 1765 as a summer retreat for British colonel Roger Morris, its distinctive style was very advanced for its time. The land on which it sits, 'Mount Morris,' commanded views of downtown Manhattan as well as of New Jersey and Westchester. During the Revolutionary War, the home was occupied by George Washington, as well as British and Hessian commanders, each drawn by the mansion's military vantage point. After the war, the mansion became Calumet Hall, a popular tavern along the Albany Post Road. In 1810, Stephen Jumel, a rich French merchant, and his wife Eliza bought the property. One year after her husband's death in 1832, Eliza married former Vice President Aaron Burr in the mansion's front parlor, but the union lasted only six months. The City bought the house from later owners, the Earles, in 1903, and it opened as a public museum the next year.", accessible: "N", historic_house_trust_website: "http://historichousetrust.org/house/morris-jumel-mansion-museum/", url: "http://morrisjumel.org", property_id: "M073", latitude: 40.8344548, longitude: -73.9385727, image_url: "https://static1.squarespace.com/static/54906edee4b033f2faec8949/551066afe4b05eaae82fc46c/552d2494e4b08bf9c26d2bc5/1429021921394/1+quater+w+land+n+blur+copy.jpg", alt_image_url: "http://newswhistle.com/wp-content/uploads/2014/11/ward1.jpg"},
  {name: "Hendrick I. Lott House", address: "1940 East 36th Street", park_name: "Hendrick I. Lott House", phone: "(718) 375-2681", description: "In 1719, family patriarch Johannes Lott purchased a farm in the rural town of Flatlands and the family quickly became leaders in their community. When Hendrick I. Lott married Mary Brownjohn in 1792, he built a larger, grander house, combining Dutch and English details into a distinctly American building. At its peak in the 19th-century, the Lotts' farm included more than 200 acres. Like most of the large farmers in southern Brooklyn, the Lotts relied on the labor of slaves, indentured servants, and hired hands to grow the crops that they sold in the markets of Brooklyn and Manhattan. However, the Lotts freed their slaves by 1805, years before the abolition of slavery in New York State in 1827. The family continued to farm at the site until 1925. The last Lott family member lived there until her death in 1989. The house and grounds are currently closed for restoration.", accessible: "N", historic_house_trust_website: "http://historichousetrust.org/house/hendrick-i-lott-house/", url: "http://www.lotthouse.org/", property_id: "B433", latitude: 40.6103011, longitude: -73.9325837, image_url: "http://www.lotthouse.org/lotthousewp2016/wp-content/uploads/2016/05/20151018_0006-700x500.jpg", alt_image_url: "http://www.lotthouse.org/lotthousewp2016/wp-content/uploads/2016/09/64-HILH-Bklyn-NY-1998-Stone-Kitchen-Final-700x500.jpg"},
  {name: "Alice Austen House Museum", address: "2 Hylan Boulevard", park_name: "Alice Austen Park", phone: "(718) 816-4506", description: "The Alice Austen House Museum on Staten Island recalls the world of an exceptional woman, photographer Alice Austen. Austen's quaint, Victorian cottage-style home, with a magnificent view of New York Harbor, displays prints from the large glass negative collection of her work that depict turn-of-the-century American life. The original house, one of the City's oldest, dates back to the 1690s. The property was bought in 1844 by John H. Austen, Alice's grandfather. Austen expanded the small, one-and-a-half-story farmhouse, and named it 'Clear Comfort'. Alice Austen was born nearby in 1866. After her father abandoned the family, she and her mother moved into her grandparents' home and Alice continued to live in the house until 1945. Austen went on to become one of the most influential photographers of the 20th Century. The City bought the Austen house in 1975, and today the Friends of Alice Austen House operates the museum.", accessible: "Y", historic_house_trust_website: "http://historichousetrust.org/house/alice-austen-house/", url: "http://aliceausten.org", property_id: "R117", latitude: 40.6151351, longitude: -74.0630179, image_url: "https://media-cdn.tripadvisor.com/media/photo-s/07/8b/75/36/alice-austen-house-museum.jpg", alt_image_url: "https://media-cdn.tripadvisor.com/media/photo-s/09/4c/b3/92/alice-austen-house-museum.jpg"},
  {name: "Kingsland Homestead", address: "143-35 37th Avenue", park_name: "Weeping Beech Park/Carman Green", phone: "(718) 939-0647", description: "Kingsland Homestead, a late 18th-century house in Flushing, stands in the shade of the Weeping Beech tree, a designated City landmark, planted in 1847. Located steps away from the 17th-century Bowne House, where Quakers were first permitted to meet in New Amsterdam, Kingsland is the headquarters of the Queens Historical Society. Charles Doughty built Kingsland around 1785, and in 1801, ownership passed to his son-in-law, British sea captain Joseph King, giving the property its name. In 1968, the three-year-old Kingsland Preservation Committee had the homestead and, threatened by area construction, transferred it to its present location.", accessible: "Y", historic_house_trust_website: "http://historichousetrust.org/house/kingsland-homestead/", url: "http://www.queenshistoricalsociety.org/kingsland-homestead-history.html", property_id: "Q022", latitude: 40.7636099, longitude: -73.8241501, image_url: "https://blankslatepages.s3.amazonaws.com/55e61ab36a667-kingsland.1007.1.jpg", alt_image_url: "http://historichousetrust.org/wp-content/uploads/2014/10/319.jpg"},
  {name: "King Manor Museum", address: "150-03 Jamaica Avenue", park_name: "King Park", phone: "(718) 206-0545", description: "King Manor is the centerpiece of an 11-acre historic park in Jamaica, Queens. The 18th- and 19th-century house takes its name from Rufus King, a signer of the United States Constitution. In 1805, Rufus King bought an existing farm that included an 18th-century Dutch-style house with an attached Long Island-style 'half house.' A year after moving in, King added a kitchen to the rear of the Dutch house. He further expanded the structure four years later to its stylish Georgian grandeur by adding a Federal dining room and two bedrooms. After King's death in 1827, his son John lived in the house and added the Greek Revival exterior details, such as the classical portico and entranceway. The house was purchased by the Village of Jamaica in 1896, and two years later, it was transferred to the City. King Manor Museum reopened to the public in 1994.", accessible: "Y", historic_house_trust_website: "http://historichousetrust.org/house/king-manor-museum/", url: "http://kingmanor.org", property_id: "Q023", latitude: 40.703686, longitude: -73.8039934, image_url: "http://kingmanor.org/wp-content/uploads/2011/09/homepage_house.jpg", alt_image_url: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT8AGzI1FuK_6sxB49TKHGQGez95B_o7GK-EbxOP0TUIaSd4OZNfA"},
  {name: "Dyckman Farmhouse Museum", address: "4881 Broadway at 204th Street", park_name: "Dyckman House Park", phone: "(212) 304-9422", description: "Not far from Inwood Hill Park at Manhattan's northern tip, the Dyckman House is the only remaining Dutch colonial farmhouse in the borough. After the Hessian occupation of the land during the American Revolution, William Dyckman built the present house in about 1785. Its southern wing, known as the 'summer kitchen', is an earlier structure that may date from 1725. In 1915, two sisters, Mary Alice D. Dean and Fannie Fredericka D. Welsh, descendants of William Dyckman, bought back the family house and presented it to the City the following year.", accessible: "N", historic_house_trust_website: "http://historichousetrust.org/house/dyckman-farmhouse-museum/", url: "http://www.dyckmanfarmhouse.org/", property_id: "M026", latitude: 40.8673996, longitude: -73.9228554, image_url: "https://michaelminn.net/newyork/museums/historic_houses/dyckman_farmhouse/2008-02-15_14-36-31.jpg", alt_image_url: "http://dyckmanfarmhouse.org/wp-content/uploads/2013/09/DFM-1866-Lithograph-Collection-Dyckman-Farmhouse-Museum-permission-needed-e1378406244640.jpg"},
  {name: "Merchant's House Museum", address: "29 East Fourth Street", park_name: "Merchant's House Museum", phone: "(212) 777-1089", description: "The Merchant's Museum is New York City's only family home preserved intact from the 19th century. A National Historic and New York City landmark, the house was built in 1832 and was home to a prosperous merchant family for 100 years. It is the City's prime example of a Greek Revival home. Complete with its original furniture, decorative arts, and personal memorabilia, it offers an intimate glimpse of domestic life during the period when New York was transformed from a colonial seaport to a thriving metropolis. An importer of hardware, Seabury Tredwell worked in the South Street Seaport and 'commuted' by horse-drawn omnibus from the Bond Street area, as the city's exclusive residential neighborhood was known in the 19th century. In 1835, he moved his large family of seven children into the red-brick and white marble rowhouse. Tredwells continued to live in the house until 1933, and the house was opened as a museum in 1936.", accessible: "N", historic_house_trust_website: "http://historichousetrust.org/house/merchants-house-museum/", url: "http://merchantshouse.org/", property_id: "M318", latitude: 40.727665, longitude: -73.99234, image_url: "http://assets.atlasobscura.com/media/W1siZiIsInVwbG9hZHMvcGxhY2VfaW1hZ2VzL2E0Y2U5Y2M1MTRjZWNjYzQ3YV9tZXJjaGFudHNob3VzZTAyLmpwZyJdLFsicCIsInRodW1iIiwieDM5MD4iXSxbInAiLCJjb252ZXJ0IiwiLXF1YWxpdHkgOTEgLWF1dG8tb3JpZW50Il1d", alt_image_url: "http://lacqueredlife.wpengine.com/wp-content/uploads/2014/04/MHM.jpg"}
])
